import sys
import random


aluy=''
l1=''
herv=''

te=aluy

output = open(sys.argv[2], 'w')

def makereads(sequence):
	reads=[]
	rcount=0
	tereads={}
	refpos=0
	first10read= True
	randcount=2000
	for i in range(0, len(sequence)-10000,1000):
		read= sequence[i:i+10000]

		if rcount == randcount:
			randcount=random.randint(500,2000)
			rcount = 0
			randint=random.randint(0,9999)
			read=sequence[i:i+randint] + te + sequence[i+randint+1:i+10000]
			#read=sequence[i:i+randint] + l1 + sequence[i+randint+1:i+10000]

			tereads[read]=randint+refpos
			first10read=False


		elif rcount < 10 and not first10read:
			interval = rcount *1000 #where in read to insert TE
			if i+randint-interval < 0 :
				read= te + sequence[i:i+10000]
				#read= te + sequence[i:i+10000]
				tereads[read]=refpos
			else:
				read=sequence[i:i+randint-interval] + te + sequence[i+randint+1-interval:i+10000]
				#read=sequence[i:i+randint-interval] + l1 + sequence[i+randint+1-interval:i+10000] #repeat several times for deeper coverage and more variants + heterozygous variants
				tereads[read]=randint-interval+refpos

		reads.append(read)
		rcount +=1
		refpos+=1000

	#print(tereads)
	return reads, tereads

chrline={}
